import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';

const Navigation = ({ user, onLogout }) => {
  const location = useLocation();
  const isAdmin = user?.role === 'admin';

  return (
    <Navbar bg="dark" variant="dark" expand="lg" className="mb-3">
      <Container fluid>
        <Navbar.Brand as={Link} to="/dashboard">
          TutoMart {isAdmin ? 'Admin' : 'Support'} Dashboard
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link as={Link} to="/dashboard" active={location.pathname === '/dashboard'}>
              Dashboard
            </Nav.Link>
            <Nav.Link as={Link} to="/tickets" active={location.pathname === '/tickets'}>
              Tickets
            </Nav.Link>
            {isAdmin && (
              <>
                <Nav.Link as={Link} to="/users" active={location.pathname === '/users'}>
                  Users
                </Nav.Link>
                <Nav.Link as={Link} to="/employees" active={location.pathname === '/employees'}>
                  Employees
                </Nav.Link>
              </>
            )}
          </Nav>
          <Nav>
            <Navbar.Text className="me-3">
              Signed in as: <strong>{user?.name}</strong> ({user?.role})
            </Navbar.Text>
            <Button variant="outline-light" size="sm" onClick={onLogout}>
              Logout
            </Button>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigation;
